def create_connection(db_file):
    """ create a database connection to the SQLite database
        specified by db_file
    :param db_file: database file
    :return: Connection object or None
    """
    conn = None
    try:
        conn = sqlite3.connect(db_file)
        return conn
    except Error as e:
        print("create_connection error" + e)

    return conn


def create_table(conn, create_table_sql):
    """ create a table from the create_table_sql statement
    :param conn: Connection object
    :param create_table_sql: a CREATE TABLE statement
    :return:
    """
    try:
        cur = conn.cursor()
        cur.execute(create_table_sql)
        conn.commit()
    except Error as e:
        print(e)

def add_bus_data(conn, bus_data_in):
    """
    Add bus data
    :param conn:
    :param bus_data_in:
    :return:
    """
    cur = conn.cursor()

    dateTimeObj = datetime.now()
    timestampStr = dateTimeObj.strftime("%d-%b-%Y (%H:%M:%S.%f)")
    print("["+timestampStr+"]: Received {0:5d} bus records".format(len(bus_data_in)))
    for bus in bus_data_in:
        # First check if record already exists
        cur.execute('SELECT * FROM bus_data WHERE (vehicle=? AND msgtime=?)',
                (bus['VEHICLE'],bus['MSGTIME']))

        result = cur.fetchone()
        # Only add if not already there
        if result is None:
            new_bus_data_tuple = (bus['VEHICLE'],
                                bus['TRIPID'],
                                bus['STOPID'],
                                bus['ROUTE'],
                                bus['MSGTIME'],
                                bus['LATITUDE'],
                                bus['LONGITUDE'],
                                bus['DIRECTION'],
                                bus['ADHERENCE'])

            sql = ''' INSERT INTO bus_data(vehicle,tripid,stopid,route,msgtime,latitude,longitude,direction,adherence)
                      VALUES(?,?,?,?,?,?,?,?,?) '''

            cur.execute(sql, new_bus_data_tuple)
            conn.commit()

def add_mac_data(conn, mac_addr, rssi):
    """
    Add mac data
    :param conn:
    :param mac_addr:
    :param rssi:
    :return:
    """
    cur = conn.cursor()

    dateTimeObj = datetime.now()
    timestampStr = dateTimeObj.strftime("%d-%b-%Y (%H:%M:%S.%f)")
    print("["+timestampStr+"]: Received {0:5d} bus records".format(len(bus_data_in)))

    # First check if record already exists
    cur.execute('SELECT * FROM mac_data WHERE (mac_addr=? AND msgtime=?)',
            (mac_addr,dateTimeObj.strftime("%d-%b-%Y (%H:%M:%S)")))

    result = cur.fetchone()
    # Only add if not already there
    if result is None:
        new_mac_data_tuple = (
                            mac_addr,
                            dateTimeObj.strftime("%d-%b-%Y (%H:%M:%S)"),
                            rssi
                            )

        sql = ''' INSERT INTO mac_data(mac_addr,msgtime,rssi)
                  VALUES(?,?,?) '''

        cur.execute(sql, new_mac_data_tuple)
        conn.commit()

